pub enum ApiError {
    FailedToDecodeReturnValue {
        function: &'static str,
        error: codec::Error,
    },
    FailedToConvertReturnValue {
        function: &'static str,
        error: codec::Error,
    },
    FailedToConvertParameter {
        function: &'static str,
        parameter: &'static str,
        error: codec::Error,
    },
    Application(Box<dyn std::error::Error + Send + Sync>),
}

pub enum InitializeBlock<'a, Block: BlockT> {
    Skip,
    Do(&'a RefCell<Option<BlockId<Block>>>),
}
